mapper.select:
SELECT *
FROM CLASS_ATTRB
where ATRB_ID 		= #{0, atrbId}
  and CLASS_ID		= #{1, classId}

mapper.insert:
insert into CLASS_ATTRB (CODE_ID, 
					CODE_VALUE, 
  				  	CODE_VALUE_NM, 
  				  	DC
  )
values (#{codeID,		jdbcType=INTEGER}, 
		#{codeValue,	jdbcType=VARCHAR}, 
  		#{codeValueNm,	jdbcType=VARCHAR}, 
  		#{dc,			jdbcType=LONGVARCHAR}
  )

mapper.update:
update CLASS_ATTRB

  	CODE_VALUE 		= #{codeValue,	jdbcType=VARCHAR},		
  	CODE_VALUE_NM 	= #{codeValueNm,jdbcType=VARCHAR},		
  	DC 				= #{dc,			jdbcType=LONGVARCHAR},  

where ATRB_ID 		= #{atrbId,		jdbcType=INTEGER}
  and CLASS_ID 		= #{classId,jdbcType=INTEGER}

mapper.delete:
delete from CLASS_ATTRB
where ATRB_ID 		= #{0, atrbId}
  and CLASS_ID		= #{1, classId}
