mapper.select:
SELECT *
FROM COMP_IFCLASS
where COMPN_ID 		= #{0, compnId}
  and IF_OPRTIN_ID	= #{1, ifOprtinId}

SELECT *
FROM COMP_IFCLASS
where COMPN_ID 		= #{0, compnId}
  and IF_OPRTIN_ID	= #{1, ifOprtinId}

mapper.insert:
insert into COMP_IFCLASS (CODE_ID, 
					CODE_VALUE, 
  				  	CODE_VALUE_NM, 
  				  	DC
  )
values (#{codeID,		jdbcType=INTEGER}, 
		#{codeValue,	jdbcType=VARCHAR}, 
  		#{codeValueNm,	jdbcType=VARCHAR}, 
  		#{dc,			jdbcType=LONGVARCHAR}
  )

mapper.update:
update COMP_IFCLASS

  	CODE_VALUE 		= #{codeValue,	jdbcType=VARCHAR},		
  	CODE_VALUE_NM 	= #{codeValueNm,jdbcType=VARCHAR},		
  	DC 				= #{dc,			jdbcType=LONGVARCHAR},  

where COMPN_ID 		= #{compnId,		jdbcType=INTEGER}
  and IF_OPRTIN_ID  = #{ifOprtinId,		jdbcType=INTEGER}

mapper.delete:
delete from COMP_IFCLASS
where COMPN_ID 		= #{0, compnId}
  and IF_OPRTIN_ID	= #{1, ifOprtinId}
