mapper.select:
SELECT *
FROM COMP_IMPL_CLASS
where INNER_CLASS_ID 		= #{0, innerClassId}
  and COMPN_ID		= #{1, compnId}

mapper.insert:
  insert into COMP_IMPL_CLASS (CODE_ID, 
  					CODE_VALUE, 
    				  	CODE_VALUE_NM, 
    				  	DC
    )
  values (#{codeID,		jdbcType=INTEGER}, 
  		#{codeValue,	jdbcType=VARCHAR}, 
    		#{codeValueNm,	jdbcType=VARCHAR}, 
    		#{dc,			jdbcType=LONGVARCHAR}
    )

mapper.update:
update COMP_IMPL_CLASS

  	CODE_VALUE 		= #{codeValue,	jdbcType=VARCHAR},		
  	CODE_VALUE_NM 	= #{codeValueNm,jdbcType=VARCHAR},		
  	DC 				= #{dc,			jdbcType=LONGVARCHAR},  

where INNER_CLASS_ID 		= #{innerClassId,		jdbcType=INTEGER}
  and COMPN_ID = #{compnId,jdbcType=INTEGER}

mapper.delete:
delete from COMP_IMPL_CLASS
where INNER_CLASS_ID 		= #{0, innerClassId}
  and COMPN_ID		= #{1, compnId}
