mapper.select:
SELECT *
FROM UDA_CLUSTER
where UC_ID 		= #{0, ucId}
  and CLASS_EN_NM	= #{1, classEnNm}

mapper.insert:
  insert into UDA_CLUSTER (CODE_ID, 
  					CODE_VALUE, 
    				  	CODE_VALUE_NM, 
    				  	DC
    )
  values (#{codeID,		jdbcType=INTEGER}, 
  		#{codeValue,	jdbcType=VARCHAR}, 
    		#{codeValueNm,	jdbcType=VARCHAR}, 
    		#{dc,			jdbcType=LONGVARCHAR}
    )

mapper.update:
update UDA_CLUSTER

  	CODE_VALUE 		= #{codeValue,	jdbcType=VARCHAR},		
  	CODE_VALUE_NM 	= #{codeValueNm,jdbcType=VARCHAR},		
  	DC 				= #{dc,			jdbcType=LONGVARCHAR},  

where UC_ID 		= #{ucId,		jdbcType=INTEGER}
  and CLASS_EN_NM = #{classEnNm,jdbcType=INTEGER}

mapper.delete:
delete from UDA_CLUSTER
where UC_ID 		= #{0, ucId}
  and CLASS_EN_NM	= #{1, classEnNm}
