mapper.select:
SELECT *
FROM LNK_IF_IEM
where INTRFC_ID 		= #{0, intrfcId}

mapper.insert:
  insert into LNK_IF_IEM (CODE_ID, 
  					CODE_VALUE, 
    				  	CODE_VALUE_NM, 
    				  	DC
    )
  values (#{codeID,		jdbcType=INTEGER}, 
  		#{codeValue,	jdbcType=VARCHAR}, 
    		#{codeValueNm,	jdbcType=VARCHAR}, 
    		#{dc,			jdbcType=LONGVARCHAR}
    )

mapper.update:
update LNK_IF_IEM
CODE_VALUE = #{codeValue, jdbcType=VARCHAR}, CODE_VALUE_NM = #{codeValueNm,jdbcType=VARCHAR}, DC = #{dc, jdbcType=LONGVARCHAR},
where INTRFC_ID = #{intrfcId, jdbcType=INTEGER}
