mapper.select:
SELECT *
FROM LNK_IF_PGM
where INTRFC_ID 		= #{0, intrfcId}
  and PGM_ID		= #{1, pgmId}

mapper.insert:
  insert into LNK_IF_PGM (CODE_ID, 
  					CODE_VALUE, 
    				  	CODE_VALUE_NM, 
    				  	DC
    )
  values (#{codeID,		jdbcType=INTEGER}, 
  		#{codeValue,	jdbcType=VARCHAR}, 
    		#{codeValueNm,	jdbcType=VARCHAR}, 
    		#{dc,			jdbcType=LONGVARCHAR}
    )

mapper.update:
update LNK_IF_PGM

  	CODE_VALUE 		= #{codeValue,	jdbcType=VARCHAR},		
  	CODE_VALUE_NM 	= #{codeValueNm,jdbcType=VARCHAR},		
  	DC 				= #{dc,			jdbcType=LONGVARCHAR},  

where INTRFC_ID 		= #{intrfcId,		jdbcType=INTEGER}
  and PGM_ID = #{pgmId,jdbcType=INTEGER}

mapper.delete:
delete from LNK_IF_PGM
where INTRFC_ID 		= #{0, intrfcId}
  and PGM_ID		= #{1, pgmId}
