mapper.select:
SELECT *
FROM LNK_IF_PGM_META
where DATA_ID 		= #{0, dataId}
  and PGM_ID		= #{1, pgmId}
  and INTRFC_ID		= #{1, intrfcId}

mapper.insert:
  insert into LNK_IF_PGM_META (CODE_ID, 
  					CODE_VALUE, 
    				  	CODE_VALUE_NM, 
    				  	DC
    )
  values (#{codeID,		jdbcType=INTEGER}, 
  		#{codeValue,	jdbcType=VARCHAR}, 
    		#{codeValueNm,	jdbcType=VARCHAR}, 
    		#{dc,			jdbcType=LONGVARCHAR}
    )

mapper.update:
update CODE_D

  	CODE_VALUE 		= #{codeValue,	jdbcType=VARCHAR},		
  	CODE_VALUE_NM 	= #{codeValueNm,jdbcType=VARCHAR},		
  	DC 				= #{dc,			jdbcType=LONGVARCHAR},  

where DATA_ID 		= #{dataId,		jdbcType=INTEGER}
  and PGM_ID = #{pgmId,jdbcType=INTEGER}
  and INTRFC_ID = #{intrfcId,jdbcType=INTEGER}

mapper.delete:
delete     FROM LNK_IF_PGM_META
where DATA_ID 		= #{0, dataId}
  and PGM_ID		= #{1, pgmId}
  and INTRFC_ID		= #{1, intrfcId}
