mapper.select:
SELECT *
FROM DMND_RELATE_BASIS
where RELATE_BASIS_ID 		= #{0, relateBasisId}

mapper.insert:
  insert into DMND_RELATE_BASIS (CODE_ID, 
  					CODE_VALUE, 
    				  	CODE_VALUE_NM, 
    				  	DC
    )
  values (#{codeID,		jdbcType=INTEGER}, 
  		#{codeValue,	jdbcType=VARCHAR}, 
    		#{codeValueNm,	jdbcType=VARCHAR}, 
    		#{dc,			jdbcType=LONGVARCHAR}
    )

mapper.update:
update DMND_RELATE_BASIS
CODE_VALUE = #{codeValue, jdbcType=VARCHAR}, CODE_VALUE_NM = #{codeValueNm,jdbcType=VARCHAR}, DC = #{dc, jdbcType=LONGVARCHAR},
where RELATE_BASIS_ID = #{relateBasisId, jdbcType=INTEGER}
