mapper.select:
SELECT *
FROM UPPER_DMND_DMND_MAP
where UPPER_DMND_ID 		= #{0, upperDmndId}
  and DMND_ID		= #{1, dmndId}

mapper.insert:
  insert into UPPER_DMND_DMND_MAP (CODE_ID, 
  					CODE_VALUE, 
    				  	CODE_VALUE_NM, 
    				  	DC
    )
  values (#{codeID,		jdbcType=INTEGER}, 
  		#{codeValue,	jdbcType=VARCHAR}, 
    		#{codeValueNm,	jdbcType=VARCHAR}, 
    		#{dc,			jdbcType=LONGVARCHAR}
    )

mapper.update:
update UPPER_DMND_DMND_MAP

  	CODE_VALUE 		= #{codeValue,	jdbcType=VARCHAR},		
  	CODE_VALUE_NM 	= #{codeValueNm,jdbcType=VARCHAR},		
  	DC 				= #{dc,			jdbcType=LONGVARCHAR},  

where CODE_ID 		= #{codeID,		jdbcType=INTEGER}
  and CODE_VALUE_ID = #{codeValueID,jdbcType=INTEGER}

mapper.delete:
delete from UPPER_DMND_DMND_MAP
where UPPER_DMND_ID 		= #{0, upperDmndId}
  and DMND_ID		= #{1, dmndId}
