mapper.select:
SELECT *
  FROM CODE_M
 where CODE_ID 		= #{0, codeID}

mapper.update:
update CODE_D
CODE_IDNTFR = #{codeIdntfr, jdbcType=INTEGER}, CD_NM = #{cdNm, jdbcType=VARCHAR}, CD_EN_NM = #{cdEnNm, jdbcType=VARCHAR}, CD_EN_ABRV_NM = #{cdEnAbrvNm, jdbcType=VARCHAR}, DC = #{dc, jdbcType=LONGVARCHAR}, DATA_TY = #{dataTy, jdbcType=VARCHAR}, DATA_LNTH = #{dataLnth, jdbcType=VARCHAR}, STD_CODE_AT = #{stdCodeAt, jdbcType=VARCHAR}, RM = #{rm, jdbcType=LONGVARCHAR}, NWD_AT = #{nwdAt, jdbcType=VARCHAR},
where CODE_ID = #{codeID,jdbcType=INTEGER}
