mapper.select:
select *
from UC_ACTOR
where ACTOR_ID = #{0, actorId}

mapper.update:
update UC_ACTOR
WORD_NM = #{wordNm, jdbcType=VARCHAR}, KR_NM = #{krNm, jdbcType=VARCHAR}, EN_NM = #{enNm, jdbcType=VARCHAR}, KR_ABRV = #{krAbrv, jdbcType=VARCHAR}, EN_ABRV = #{enAbrv, jdbcType=VARCHAR}, WORD_APPLC_SCOPE = #{wordApplcScope, jdbcType=VARCHAR}, ETC_MATTER_DC = #{etcMatterDc, jdbcType=VARCHAR}, WORD_DFN = #{wordDfn, jdbcType=LONGVARCHAR},
where WORD_ID = #{wordId,jdbcType=INTEGER}
